import React, { Component } from "react";
import ReactDOM from "react-dom";
import './App.css';
import Button from "./components/Button";
import Input from "./components/Input";
import ClearButton from "./components/ClearButton";
class MyForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        input: "",
        previousNumber: "",
        currentNumber: "",
        operator: "",
      };
    }

    addtoInput = val => {
      this.setState({ input: this.state.input +val });
    }

    render() {
      return(
        <div className="App">
          <div className="calc-wrapper">
            <div className="row">
              <Input>{this.state.input}</Input>
            </div>
            <div className="row">
              <Button>del</Button>
              <ClearButton>clear</ClearButton>
              <Button>=</Button>
            </div>
            <div className="row">
              <Button handleClick={this.addtoInput} >7</Button>
              <Button>8</Button>
              <Button>9</Button>
              <Button>/</Button>
            </div>
            <div className="row">
              <Button>4</Button>
              <Button>5</Button>
              <Button>6</Button>
              <Button>X</Button>
            </div>
            <div className="row">
              <Button>1</Button>
              <Button>2</Button>
              <Button>3</Button>
              <Button>-</Button>
            </div>
            <div className="row">
              <Button>0</Button>
              <Button>.</Button>
              <Button>+</Button>
            </div>
          </div>
        </div>
      );

    }
  }

  ReactDOM.render(<MyForm />, document.getElementById('root'));
